mapper.update:
UPDATE t_shopping_cart SET is_delete = 1 WHERE id IN
#{id}
AND account_id = #{accountId} AND is_delete = 0

UPDATE t_shopping_cart t
INNER JOIN t_goods_sku t1 ON t1.id = t.sku_id
AND t1.is_delete = 0
SET amount = #{amount}
WHERE
    t.id = #{cartId}
    AND t.account_id = #{accountId}
    AND t1.quantity >= #{amount}
